    private void emailTextFieldKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_emailTextFieldKeyReleased
        // TODO add your handling code here:
        String regex = "^[\\w!#$%&’*+/=?`{|}~^-]+(?:\\.[\\w!#$%&’*+/=?`{|}~^-]+)*@(?:[a-zA-Z0-9-]+\\.)+[a-zA-Z]{2,6}$";
        Pattern p = Pattern.compile(regex);
        Matcher m = p.matcher(emailTextField.getText());

        if (m.find()) {
            emailValidationLabel.setText("");
        } else {
            emailValidationLabel.setText("Use complete email address");
        }
    }//GEN-LAST:event_emailTextFieldKeyReleased

    private void phoneTextFieldKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_phoneTextFieldKeyReleased
        // TODO add your handling code here:
        String regex = "^(92)[0-9]{10}$";
        Pattern p = Pattern.compile(regex);
        Matcher m = p.matcher(phoneTextField.getText());

        if (m.find()) {
            phoneValidationLabel.setText("");
        } else {
            phoneValidationLabel.setText("format: (923XXXXXXXXXX)");
        }

    }//GEN-LAST:event_phoneTextFieldKeyReleased

