    private void userTextFieldKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_userTextFieldKeyReleased
        // TODO add your handling code here:
        String regex = "^[A-Za-z][A-Za-z0-9_]{7,19}$";
        Pattern p = Pattern.compile(regex);
        Matcher m = p.matcher(userTextField.getText());

        if (m.find()) {
            userValidationLabel.setText("");
        } else {
            userValidationLabel.setText("Minimum 8 characters");
        }
    }//GEN-LAST:event_userTextFieldKeyReleased

    private void passwordFieldKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_passwordFieldKeyReleased
        // TODO add your handling code here:
        String regex = "^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9])(?=.*?[#?!@$%^&*-]).{8,}$";
        Pattern p = Pattern.compile(regex);
        Matcher m = p.matcher(passwordField.getText());

        if (m.find()) {
            passwordValidationLabel.setText("");
        } else {
            passwordValidationLabel.setText("[Minimum 8 characters]     Hint: Abc1$");
        }
    }//GEN-LAST:event_passwordFieldKeyReleased

    private void signUpButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_signUpButtonActionPerformed
        // TODO add your handling code here:
        DB_Connectivity db = new DB_Connectivity();//object

        if (passwordField.getText().matches(cPasswordField.getText())) {
            if (db.signupUser(nameTextField.getText(), userTextField.getText(), emailTextField.getText(), Long.parseLong(phoneTextField.getText()), passwordField.getText()) == 1) {
                JOptionPane.showMessageDialog(this, "Successfully! Account has been created", "Registration", 1);
                LogIn lf = new LogIn();
                lf.setVisible(true);
                dispose();
               
            } else {
                JOptionPane.showMessageDialog(this, "Error", "Registration", 2);
            }
        } else {
            JOptionPane.showMessageDialog(this, "Password Not Matched", "Registration", 2);
        }
    }//GEN-LAST:event_signUpButtonActionPerformed

